const Graph = () => {
    return (
        <><div class="row">

            <div class="column">
                <button id="front" class="stack choice"> FRONT END </button>
            </div>
            <div class="column">
                <button id="back" class="stack"> BACK END </button>
            </div>
        </div><section class="bar-graph bar-graph-horizontal bar-graph-one active">

                <div class="bar-container">
                    <span class="lang" data-alt="C#">JAVASCRIPT</span>
                    <div class="bar" data-percentage="60%" data-alt="80%"></div>
                </div>
                <div class="bar-container">
                    <span class="lang" data-alt="SQL">CSS</span>
                    <div class="bar" data-percentage="70%" data-alt="75%"></div>
                </div>
                <div class="bar-container">
                    <span class="lang" data-alt="PYTHON">HTML</span>
                    <div class="bar" data-percentage="80%" data-alt="50%"></div>
                </div>
                <div class="bar-container">
                    <span class="lang" data-alt="JAVA">REACT</span>
                    <div class="bar" data-percentage="40%" data-alt="70%"></div>
                </div>
            </section></>
        
        
    )
}
$(".bar").each(function (index) {
    var delay = (index) * 200;
    var bwidth = ($(this).data("percentage"));
    console.log(bwidth);
    $(this).delay(delay).animate({
        width: bwidth,

    }, 1000);
});

$(".stack").click(function () {
    if (!$(this).hasClass("choice")) {

        $(".stack").toggleClass("choice");
        $(".lang").each(function (index) {
            var delay = (index) * 500;
            titleSwap($(this));

        });
        $(".bar").each(function (index) {
            var delay = (index) * 50;
            widthSwap($(this));
        });
    };

});

function titleSwap(span, delay) {

    var temp = $(span).text();
    $(span).delay(delay).animate({
        opacity: 0,

    }, 200, function () {
        $(span).text($(span).data("alt"));
        $(span).data("alt", temp);
        $(span).delay(delay).animate({
            opacity: 1,

        }, 200,);
    });



};

function widthSwap(bar, delay) {
    var temp = $(bar).data("percentage");
    $(bar).delay(delay).animate({
        width: $(bar).data("alt"),

    }, 200
    );
    $(bar).data("percentage", $(bar).data("alt"));
    $(bar).data("alt", temp);
    $(bar).toggleClass("alt");


};

export default Graph
