const Graph = () => {
    return (
        <div id="app"></div>
    )
}
const Filler = props => {
    return (
        <div
            className="filler"
            style={{ width: `${props.percentage}%` }}
        />
    );
};

// ----------------------------------
// ----- PROGRESS BAR COMPONENT -----
// ----------------------------------
class ProgressBar extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            percentage: 0
        };
    }

    render() {
        return (
            <div className="progress-bar">
                <div className="percentage">{`${this.props.percentage}%`}</div>
                <Filler percentage={this.props.percentage} />
            </div>
        );
    }
}

// ---------------------------------
// ----- BAR DISPLAY COMPONENT -----
// ---------------------------------
const programmingLanguages = [
    { type: 'HTML', percentage: 75 },
    { type: 'CSS/Sass', percentage: 75 },
    { type: 'Java', percentage: 50 },
    { type: 'JavaScript', percentage: 70 },
    { type: 'SQL', percentage: 60 },
    { type: 'Swift', percentage: 50 }
];

function renderBars() {
    return _.map(programmingLanguages, language => {
        return (
            <div className="bar-container">
                <p className="skill-title">
                    {language.type}
                </p>
                <ProgressBar percentage={language.percentage} />
            </div>
        );
    });
}

const ProgrammingLanguageBars = () => {
    return (
        <div className="container">
            <h2 className="title">Programming Languages</h2>
            {renderBars()}
        </div>
    );
};

// Render the component
ReactDOM.render(
    <ProgrammingLanguageBars />,
    document.getElementById('app')
);
export default Graph
