import React from "react";
import Carousel from '@brainhubeu/react-carousel';
import '@brainhubeu/react-carousel/lib/style.css';
import Slides from "./Slides";
import "./Slider.css";

const Slider = () => {
  return (
    <div className="carousel-container">
        <div className="carousel-tittle">
            <h2>Mis proyectos</h2>
        </div>
    
        <Carousel
            arrows
            slidesPerPage ={3}
            infinite
            animationSpeed={200}
            centered
            offset={25}
            itemWidth = {400}
            slides={Slides}
            breakpoints = {{
              960: {
                slidesPerPage: 1,
                arrows: false,
                itemWidth:250,
                arrows:true,
              }
            }}
            plugins={[
              {
                resolve: arrowsPlugin,
                options: {
                  arrowLeft: <button><Icon name="angle-double-left" /></button>,
                  arrowLeftDisabled: <button><Icon name="angle-left" /></button>,
                  arrowRight: <button><Icon name="angle-double-right" /></button>,
                  arrowRightDisabled: <button><Icon name="angle-right" /></button>,
                  addArrowClickHandler: true,
                }
              }
            ]}
            
        />
    </div>
  )
}

export default Slider;
